// Make the showSidebar function into a variable so it can be easily called
var showSidebar=function(){$("body").toggleClass("active")};jQuery(window).resize(function(){var e=$(".off-canvas-navigation").css("display");e==="block"&&$("body").removeClass("active")});jQuery(document).ready(function(e){e("#sidebar_button").click(function(e){e.preventDefault();showSidebar()})});